query Q_GraultDetail($config: QueryConfigInput!)
    {
        iQueryGrault(config: $config)
        {
            type
            columnStates{
                name
                enabled
                sortable
            }
            queryConfig{
                id
                condition
                offset
                pageSize
                sortFields
            }
            rows{
                id
                name
                attachmentId
                url
                attachment{
                    id
                    type
                    description
                    url
                }
            }
            rowCount
        }
    }
