observables.defaultValue:
injection( // language=GraphQL
`query iQueryFoo($config: QueryConfigInput!)
        {
            iQueryFoo(config: $config)
            {
                type
                columnStates{
                    name
                    enabled
                    sortable
                }
                queryConfig{
                    id
                    condition
                    currentPage
                    pageSize
                    sortFields
                }
                rows{
                    id
                    name
                    description
                    flag
                    type
                    owner{
                        id
                        login
                    }
                }
                rowCount
            }
        }`, { "config": { "pageSize": 20 } })

injection( // language=GraphQL
`query iQueryFooType($config: QueryConfigInput!)
        {
            iQueryFooType(config: $config)
            {
                rows{
                    name
                }
                rowCount
            }
        }`, { "config": { // deactivate paging
"pageSize": 0 } })

extraConstants:
// language=GraphQL
const FooDetailQuery = new GraphQLQuery(`query queryFooDetail($config: QueryConfigInput!)
{
    iQueryFoo(config: $config)
    {
        rows{
            id
            name
            num
            description
            created
            flag
            type
            owner{
                id
                login
            }
        }
    }
}`); // noinspection JSUnusedGlobalSymbols
