query iQueryFoo($config: QueryConfigInput!)
    {

        iQueryFoo(config: $config)
        {
            type
            columnStates{
                name
                enabled
                sortable
            }
            queryConfig{
                id
                condition
                offset
                pageSize
                sortFields
            }
            rows{
                id
                name
                description
                num

                owner{
                    login
                }
            }
            rowCount
        }
    }
